// Material UI components
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";

// Icons
import ThunderstormIcon from "@mui/icons-material/Thunderstorm";
import AcUnitIcon from "@mui/icons-material/AcUnit";
import WbSunnyIcon from "@mui/icons-material/WbSunny";

// CSS
import "./InfoBox.css";

export default function InfoBox({ info }) {

  // Default image (fallback – not used directly now but safe to keep)
  const INIT_URL =
    "data:image/jpeg;base64,"; // (same as yours, shortened here)

  // Weather based images
  const HOT_URL =
    "data:image/jpeg;base64,";

  const COLD_URL =
    "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTVnTKgwHmzQObDoLG9D-9HMRJYOGhCMzJY0A&s";

  const RAIN_URL =
    "data:image/jpeg;base64,";

  // Decide image based on weather conditions
  const weatherImage =
    info.humidity > 80
      ? RAIN_URL
      : info.temp > 15
      ? HOT_URL
      : COLD_URL;

  // Decide icon based on weather conditions
  const weatherIcon =
    info.humidity > 80 ? (
      <ThunderstormIcon />
    ) : info.temp > 15 ? (
      <WbSunnyIcon />
    ) : (
      <AcUnitIcon />
    );

  return (
    <div className="InfoBox">
      <div className="cardContainer">

        {/* Weather Card */}
        <Card sx={{ maxWidth: 345 }}>

          {/* Image section */}
          <CardMedia
            sx={{ height: 140 }}
            image={weatherImage}
            title="weather image"
          />

          {/* Content section */}
          <CardContent>

            {/* City name + icon */}
            <Typography gutterBottom variant="h5">
              {info.city} {weatherIcon}
            </Typography>

            {/* Weather details */}
            <Typography variant="body2" sx={{ color: "text.secondary" }}>

              <p>Temperature = {info.temp}°C</p>
              <p>Humidity = {info.humidity}%</p>
              <p>Feels Like = {info.feelsLike}°C</p>
              <p>Min Temp = {info.tempMin}°C</p>
              <p>Max Temp = {info.tempMax}°C</p>

              <p>
                The weather can be described as{" "}
                <b>{info.weather}</b> and feels like{" "}
                <b>{info.feelsLike}°C</b>
              </p>

            </Typography>
          </CardContent>

        </Card>
      </div>
    </div>
  );
}
